(ns euler.p00 
	(:require [euler.fns :as f]))

(defn p1 []
	(reduce + 
		(filter #(f/any-divisible? % [3 5]) (range 1000))))

(defn p2 []
	(->> (take-while #(< % 4000000) (f/fibonacci))
		(filter even?)
		(apply +)))
		
(defn p3 []
	(apply max (f/factor 600851475143)))
	
(defn p4 []
	(->> (for [a (range 1 1000) b (range 1 1000)] (* a b))
		(filter f/palindrome?)
		(apply max)))
		
(defn p5 []
	(->> (map #(frequencies (f/factor %)) (range 2 21))
		(reduce #(merge-with max % %2))
		(map #(repeat (val %) (key %)))
		flatten (apply *')))
		
(defn p6 []
	(let [r (range 101) 
		a (f/sq (apply + r))
		b (apply + (map f/sq r))]
		(bigint (- a b))))
		
(defn p7 []
	(nth (f/primes) 10000))
	
(defn p8 []
	(let [N ]
	(apply max
	(for [group (partition 5 1 (str N))]
		(->> group
			(map #(Integer/parseInt (str %)))
			(apply *))))))
			
(defn p9 []	; TODO very slow
	(let [r (range 1 500)]
		(for [a r b r c r :when (and (f/pythagorean? a b c) (= 1000 (+ a b c)))]
			(* a b c))))
